mapper.select:
SELECT hi.apt_seq AS aptSeq, hi.apt_nm AS aptName, MAX(hd.floor) AS floor, AVG(hd.exclu_use_ar) AS excluUseAr, hi.umd_nm AS legalDong, MAX(hd.deal_amount) AS tradeAmount, hi.latitude AS latitude, hi.longitude AS longitude FROM housedeals hd JOIN houseinfos hi ON hd.apt_seq = hi.apt_seq JOIN dongcodes dc ON hi.umd_nm = dc.dong_name WHERE dc.sido_name = #{sido} AND dc.gugun_name = #{gugun} AND dc.dong_name = #{dong} AND CONCAT(hi.sgg_cd, hi.umd_cd) = dc.dong_code
AND hd.deal_year = #{year}
AND hd.deal_month = #{month}
GROUP BY hi.apt_seq, hi.apt_nm

SELECT
distinct(deal_year) AS deal_year
FROM housedeals hd
JOIN houseinfos hi ON hd.apt_seq = hi.apt_seq
JOIN dongcodes dc ON
hi.umd_nm = dc.dong_name
WHERE dc.sido_name = #{sido}
AND dc.gugun_name
= #{gugun}
AND dc.dong_name = #{dong}
AND CONCAT(hi.sgg_cd, hi.umd_cd) =
dc.dong_code
ORDER BY deal_year;

SELECT
distinct(deal_month) AS deal_month
FROM housedeals hd
JOIN houseinfos hi ON hd.apt_seq = hi.apt_seq
JOIN dongcodes dc ON
hi.umd_nm = dc.dong_name
WHERE dc.sido_name = #{sido}
AND dc.gugun_name
= #{gugun}
AND dc.dong_name = #{dong}
AND CONCAT(hi.sgg_cd, hi.umd_cd) =
dc.dong_code
AND hd.deal_year = #{year}
ORDER BY deal_month;
