mapper.select:
SELECT idx, userid AS userId, title, content, date, answer, answer_date AS answerDate
FROM qna
ORDER BY date DESC;

mapper.update:
UPDATE qna
SET answer = #{answer}, answer_date = NOW()
WHERE idx = #{idx};

UPDATE qna
SET title = #{title}, content = #{content}
WHERE idx = #{idx};

UPDATE qna
SET answer = NULL, answer_date = NULL
WHERE idx = #{idx};
