mapper.select:
SELECT id, password, email, name, age, date, profile_image
FROM user
ORDER BY id ASC;

SELECT id, password, email, name, age, date, profile_image
FROM user
WHERE
id = #{id}
AND password = #{password}
AND is_deleted = FALSE

mapper.update:
UPDATE user
SET password = #{password}, name = #{name}, email = #{email}, age =
#{age}
WHERE id = #{id}

UPDATE user
SET password = #{newPassword}
WHERE id = #{userId} AND password = #{currentPassword}
AND is_deleted = FALSE

UPDATE user
SET profile_image = #{profileImage}
WHERE id = #{userId}
AND is_deleted = FALSE

UPDATE user
SET profile_image = #{profileImage}
WHERE id = #{id}
